import { createRouter, createWebHistory } from 'vue-router';
import store from '../store/index';

const routes = [
  {
    path: '/',
    name: 'home',
    component: () => import('../views/HomeView.vue'),
    meta: { title: 'Home' }
  },
  {
    path: '/books',
    name: 'books',
    component: () => import('../views/BooksView.vue'),
    meta: { title: 'Books' }
  },
  {
    path: '/book/:book_id',
    name: 'book',
    component: () => import('../views/BookView.vue'),
    meta: { title: 'Book Details' }
  },
  {
    path: '/register',
    name: 'register',
    component: () => import('../views/RegisterView.vue'),
    meta: { title: 'Register' }
  },
  {
    path: '/login',
    name: 'login',
    component: () => import('../views/LoginView.vue'),
    meta: { title: 'Log In' }
  },
  {
    path: '/bookmarks',
    name: 'bookmarks',
    component: () => import('../views/LikedBooksView.vue'),
    meta: { requiresAuth: true, title: 'Bookmarks' }
  },
  {
    path: '/likeGenres',
    name: 'likeGenres',
    component: () => import('../views/GenresULikeView.vue'),
    meta: { requiresAuth: true, title: 'Like Genres' }
  },
  {
    path: '/foryou',
    name: 'foryou',
    component: () => import('../views/RecomendedBooksView.vue'),
    meta: { requiresAuth: true, title: 'Recomendations' }
  },
  {
    path: '/:catchAll(.*)',
    redirect: '/'
  }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
});

router.beforeEach((to, from, next) => {
  const isLoggedIn = store.getters.isUserLogedIn;
  const user = store.getters.user;

  if (to.meta.title) {
    document.title = to.meta.title;
  }

  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isLoggedIn) {
      next({ name: 'login' });
    } else if (to.matched.some(record => record.meta.requiresRole) && (!user || user.username !== 'adminDarko')) {
      next({ name: 'home' });
    } else {
      next();
    }
  } else {
    next();
  }
});

export default router;
