import { createApp } from 'vue'
import store from './store/index' 
import App from './App.vue'
import router from './router'

const app = createApp(App)

router.afterEach((to) => {
    store.dispatch('updateRoute', {
      path: to.path,
      query: to.query,
      params: to.params
    });
  });
  
app.use(store).use(router).mount('#app')
